// Global variables for our simulation
let processes = []; // Array of waiting processes
let nextProcessId = 1;
let score = 0;
let scoreText;
let instructionText;
let cpuText;
let processGroup;

function preload() {
  // No external assets in this demo.
}

function create() {
  // Create a group to hold process display objects.
  processGroup = this.add.group();

  // Display score and instructions.
  scoreText = this.add.text(16, 16, 'Score: 0', { font: '20px Arial', fill: '#000' });
  instructionText = this.add.text(
    16,
    50,
    'Select the process with the shortest burst time (SJF) by pressing 1, 2, 3, ...',
    { font: '16px Arial', fill: '#000' }
  );

  // Display CPU area in the middle.
  cpuText = this.add.text(400, 300, 'CPU Idle', { font: '24px Arial', fill: '#d32f2f' }).setOrigin(0.5);

  // Generate the initial waiting queue of processes.
  generateProcesses(this);

  // Listen for keyboard events (number keys for selection).
  this.input.keyboard.on('keydown', handleKey, this);
}

function update() {
  // In this simulation, our logic is event-driven (no per-frame updates needed).
}

/**
 * Generates a new set of processes with random burst times.
 * Each process is represented as an object with an id and burstTime.
 * The processes are displayed as rectangles with labels.
 */
function generateProcesses(scene) {
  // Clear any existing process display objects.
  processGroup.clear(true, true);
  processes = [];

  // Generate between 3 and 5 processes.
  const numProcesses = Phaser.Math.Between(3, 5);
  for (let i = 0; i < numProcesses; i++) {
    const burst = Phaser.Math.Between(2, 10);
    let proc = {
      id: nextProcessId++,
      burstTime: burst
    };
    processes.push(proc);
  }

  // Sort processes by id (so the order of display is consistent).
  processes.sort((a, b) => a.id - b.id);

  // Display each process in a "waiting queue" area.
  for (let i = 0; i < processes.length; i++) {
    let proc = processes[i];
    // Set a display position for the process.
    let x = 100 + i * 150;
    let y = 100;

    // Draw a rectangle to represent the process.
    let rect = scene.add.rectangle(x, y, 120, 80, 0x90caf9).setOrigin(0.5);
    // Add text inside the rectangle with process id and burst time.
    let text = scene.add.text(x, y, `P${proc.id}\nBurst: ${proc.burstTime}`, {
      font: '20px Arial',
      fill: '#000',
      align: 'center'
    }).setOrigin(0.5);
    // Add an index label (for selection via number keys) near the rectangle.
    let indexText = scene.add.text(x - 50, y - 30, `${i + 1}`, {
      font: '24px Arial',
      fill: '#d32f2f'
    });

    // Save references to these display objects in the process object.
    proc.rect = rect;
    proc.text = text;
    proc.indexText = indexText;

    // Add these objects to the group.
    processGroup.addMultiple([rect, text, indexText]);
  }
}

/**
 * Handles keyboard input.
 * When the player presses a number key, the corresponding process in the waiting queue is selected.
 * The correct process is the one with the smallest burst time.
 */
function handleKey(event) {
  // Convert the pressed key to an index (assuming keys "1", "2", "3", etc.).
  let index = parseInt(event.key) - 1;
  if (isNaN(index)) return;
  if (index < 0 || index >= processes.length) return;

  // Determine the correct process (one with the shortest burst time).
  let correctProcess = processes.reduce((prev, curr) => (curr.burstTime < prev.burstTime ? curr : prev), processes[0]);

  let selectedProcess = processes[index];

  if (selectedProcess === correctProcess) {
    // Correct selection: simulate the process running in the CPU.
    cpuText.setText(`CPU Running P${selectedProcess.id}`);
    score += 10;
    scoreText.setText('Score: ' + score);

    // Remove the selected process's display objects with a small delay.
    selectedProcess.rect.destroy();
    selectedProcess.text.destroy();
    selectedProcess.indexText.destroy();

    // Remove it from the waiting queue array.
    processes.splice(index, 1);

    // Simulate the process run time (1 second delay).
    this.time.delayedCall(1000, () => {
      cpuText.setText('CPU Idle');
      // If no processes remain, generate a new batch.
      if (processes.length === 0) {
        generateProcesses(this);
      } else {
        // Re-label and re-position the remaining processes for clarity.
        for (let i = 0; i < processes.length; i++) {
          processes[i].indexText.setText(`${i + 1}`);
          let newX = 100 + i * 150;
          processes[i].rect.x = newX;
          processes[i].text.x = newX;
          processes[i].indexText.x = newX - 50;
        }
      }
    }, [], this);
  } else {
    // Incorrect selection: show an error message.
    cpuText.setText('Incorrect! Try Again.');
    this.time.delayedCall(1000, () => {
      cpuText.setText('CPU Idle');
    }, [], this);
  }
}

