/**
 * Generates a new question by picking a random float, converting it to IEEE754,
 * generating two distractors by altering one random bit, shuffling the options,
 * and then displaying the question and answers.
 */
function generateIEEE754Question(scene) {
  // Generate a random float between -50 and 50 (rounded to 2 decimals).
  currentNumber = Phaser.Math.FloatBetween(-50, 50);
  currentNumber = parseFloat(currentNumber.toFixed(2));

  // Compute the correct IEEE754 (32-bit) representation.
  correctAnswer = floatToIEEE754(currentNumber);

  // Create two distractors by flipping a random bit in the correct answer.
  let distractor1 = alterBit(correctAnswer);
  let distractor2 = alterBit(correctAnswer);

  // Ensure that the distractors differ from the correct answer and from each other.
  while (distractor1 === correctAnswer) {
    distractor1 = alterBit(correctAnswer);
  }
  while (distractor2 === correctAnswer || distractor2 === distractor1) {
    distractor2 = alterBit(correctAnswer);
  }

  // Create an array of answer options and shuffle them.
  currentAnswers = [correctAnswer, distractor1, distractor2];
  Phaser.Utils.Array.Shuffle(currentAnswers);

  // Update the question text.
  questionText.setText(`Convert ${currentNumber} to IEEE754 (32-bit):`);

  // Remove any previous answer texts.
  if (answerTexts.length > 0) {
    answerTexts.forEach(text => text.destroy());
    answerTexts = [];
  }

  // Display the answer options (with labels 1, 2, 3).
  for (let i = 0; i < currentAnswers.length; i++) {
    let option = scene.add.text(400, 250 + i * 50, `${i + 1}: ${currentAnswers[i]}`, {
      font: '20px Arial',
      fill: '#000'
    }).setOrigin(0.5);
    answerTexts.push(option);
  }

  questionActive = true;
}

/**
 * Handles number key presses.
 * If the correct answer is chosen, the score increases; otherwise, a message is shown.
 * After a short delay, a new question is generated.
 */
function handleKey(event) {
  if (!questionActive) return;

  // Convert the key to an index (assuming keys "1", "2", "3", etc.).
  const index = parseInt(event.key) - 1;
  if (index < 0 || index >= currentAnswers.length) return;

  const selectedAnswer = currentAnswers[index];

  if (selectedAnswer === correctAnswer) {
    score += 10;
    scoreText.setText('Score: ' + score);
    questionText.setText('Correct!');
  } else {
    questionText.setText('Incorrect!');
  }

  questionActive = false;
  // Wait 1 second, then generate a new question.
  this.time.delayedCall(1000, () => {
    generateIEEE754Question(this);
  }, [], this);
}

/**
 * Converts a floating-point number to its IEEE754 32-bit binary representation.
 * Uses a Float32Array and a Uint32Array to extract the underlying bits.
 */
function floatToIEEE754(num) {
  let f32 = new Float32Array(1);
  f32[0] = num;
  let u32 = new Uint32Array(f32.buffer);
  // Convert to a binary string, padded to 32 bits.
  return u32[0].toString(2).padStart(32, '0');
}

/**
 * Flips a random bit in a 32-bit binary string.
 * This function returns a new string with one bit toggled.
 */
function alterBit(binaryStr) {
  // Pick a random index between 0 and 31.
  const index = Phaser.Math.Between(0, 31);
  // Determine the new bit value.
  const newBit = binaryStr[index] === '0' ? '1' : '0';
  // Return the string with the bit at the selected index flipped.
  return binaryStr.substr(0, index) + newBit + binaryStr.substr(index + 1);
}

