/* --------------------------
   LOCK 1: Door Lock Puzzle
   --------------------------
   Puzzle: Convert a negative decimal number to its 8-bit 2's complement representation.
   Example: If lock1Number = -5, then 2's complement (8-bit) is 11111011.
*/
function setupLock1(scene) {
  puzzleStage = 1;
  // Clear any old options.
  clearOptions();

  // Generate a random negative integer between -128 and -1.
  lock1Number = Phaser.Math.Between(-128, -1);
  // Compute 8-bit 2's complement: For a negative number, add 256.
  let twosValue = 256 + lock1Number; // This is an integer between 128 and 255.
  lock1Correct = twosValue.toString(2).padStart(8, '0'); // 8-bit binary string.

  // Generate two distractors by flipping one random bit.
  let distractor1 = alterBit(lock1Correct);
  let distractor2 = alterBit(lock1Correct);
  // Make sure distractors differ from correct and from each other.
  while (distractor1 === lock1Correct) {
    distractor1 = alterBit(lock1Correct);
  }
  while (distractor2 === lock1Correct || distractor2 === distractor1) {
    distractor2 = alterBit(lock1Correct);
  }

  currentAnswers = [lock1Correct, distractor1, distractor2];
  Phaser.Utils.Array.Shuffle(currentAnswers);

  // Update narrative text.
  narrativeText.setText(
    `You approach a heavy door lock.\n\n` +
    `A panel displays the decimal number:\n  ${lock1Number}\n\n` +
    `To pop the lock, convert this number into its 8-bit 2's complement representation.`
  );

  // Display answer options.
  for (let i = 0; i < currentAnswers.length; i++) {
    let opt = scene.add.text(400, 250 + i * 60, `${i + 1}: ${currentAnswers[i]}`, {
      font: '22px Arial',
      fill: '#3e2723'
    }).setOrigin(0.5);
    optionTexts.push(opt);
  }
}

/* --------------------------
   LOCK 2: Safe Lock Puzzle
   --------------------------
   Puzzle: Divide the number from Lock 1 (lock1Number) by a random positive number.
   The positive number is given in IEEE754 32-bit binary string form.
   You must decode it and then compute (lock1Number / decoded value), rounded to two decimals.
*/
function setupLock2(scene) {
  puzzleStage = 2;
  clearOptions();

  // Generate a random positive float between 1 and 20.
  let randomFloat = Phaser.Math.FloatBetween(1, 20);
  // Round for display purposes.
  randomFloat = parseFloat(randomFloat.toFixed(2));

  // Convert it to IEEE754 32-bit binary representation.
  ieee754Str = floatToIEEE754(randomFloat);
  ieee754Value = randomFloat; // For clarity.

  // Compute the division: (lock1Number) divided by (randomFloat).
  // Note: lock1Number is negative.
  divisionResult = (lock1Number / randomFloat).toFixed(2);

  // Generate two distractor answers by adding/subtracting a small random offset.
  let offset1 = (Phaser.Math.FloatBetween(0.1, 1)).toFixed(2);
  let offset2 = (Phaser.Math.FloatBetween(0.1, 1)).toFixed(2);

  // Randomly decide to add or subtract.
  let distractor1 = (parseFloat(divisionResult) + (Math.random() < 0.5 ? +offset1 : -offset1)).toFixed(2);
  let distractor2 = (parseFloat(divisionResult) + (Math.random() < 0.5 ? +offset2 : -offset2)).toFixed(2);

  // Ensure uniqueness.
  if (distractor1 === divisionResult) distractor1 = (parseFloat(divisionResult) + 0.1).toFixed(2);
  if (distractor2 === divisionResult || distractor2 === distractor1) distractor2 = (parseFloat(divisionResult) - 0.1).toFixed(2);

  currentAnswers = [divisionResult, distractor1, distractor2];
  Phaser.Utils.Array.Shuffle(currentAnswers);

  // Update narrative text.
  narrativeText.setText(
    `Inside the vault, a safe awaits.\n\n` +
    `The safe displays an IEEE754 32-bit binary string:\n  ${ieee754Str}\n\n` +
    `Decode this to reveal a positive number (${ieee754Value}).\n` +
    `Now, divide the door lock number (${lock1Number}) by this number.\n` +
    `Provide the result (rounded to two decimals) to open the safe.`
  );

  // Display answer options.
  for (let i = 0; i < currentAnswers.length; i++) {
    let opt = scene.add.text(400, 250 + i * 60, `${i + 1}: ${currentAnswers[i]}`, {
      font: '22px Arial',
      fill: '#3e2723'
    }).setOrigin(0.5);
    optionTexts.push(opt);
  }
}

/* --------------------------
   Input Handler: handleKey
   --------------------------
   Listens for number key presses (1, 2, 3) to select an answer.
   If correct, advances the game; if incorrect, provides feedback.
*/
function handleKey(event) {
  let key = event.key;
  let index = parseInt(key) - 1;
  if (isNaN(index) || index < 0 || index >= currentAnswers.length) return;

  // Check answer based on the current puzzle stage.
  if (puzzleStage === 1) {
