function preload() {
  // Create simple textures using graphics
  this.graphics = this.add.graphics();

  // Player texture: a blue square
  this.graphics.fillStyle(0x2196f3, 1);
  this.graphics.fillRect(0, 0, 32, 32);
  this.graphics.generateTexture('player', 32, 32);
  this.graphics.clear();

  // Door lock texture: a brown rectangle
  this.graphics.fillStyle(0x6d4c41, 1);
  this.graphics.fillRect(0, 0, 48, 48);
