  scoreText = this.add.text(16, 16, 'Score: 0', { font: '20px Arial', fill: '#000' });
  narrativeText = this.add.text(400, 50, 'Explore the world.\nUse arrow keys to move.\nPress E to interact with nearby objects.', 
    { font: '20px Arial', fill: '#000', align: 'center' }).setOrigin(0.5);

