  if (mode === "exploration") {
    // --- Player Movement ---
    player.setVelocityX(0);
    if (cursors.left.isDown) {
      player.setVelocityX(-160);
    } else if (cursors.right.isDown) {
      player.setVelocityX(160);
    }
    if (cursors.up.isDown && player.body.touching.down) {
      player.setVelocityY(-330);
    }

    // --- Interaction Check ---
    // Check if player is near door lock (and door lock not yet solved).
    if (!doorLockSolved && Phaser.Math.Distance.Between(player.x, player.y, doorLock.x, doorLock.y) < 70) {
      narrativeText.setText('Press E to interact with the Door Lock.');
      if (Phaser.Input.Keyboard.JustDown(interactKey)) {
        startPuzzle(1, this);
      }
    }
    // Check if door lock is solved and safe is available.
    else if (doorLockSolved && !safeSolved && Phaser.Math.Distance.Between(player.x, player.y, safe.x, safe.y) < 70) {
      narrativeText.setText('Press E to interact with the Safe.');
      if (Phaser.Input.Keyboard.JustDown(interactKey)) {
        startPuzzle(2, this);
      }
    }
    else {
      narrativeText.setText('Explore the world.\nUse arrow keys to move.\nPress E to interact with nearby objects.');
    }
  }
  // When in puzzle mode, movement is disabled.
}

