--------------------------- */
function startPuzzle(stage, scene) {
  mode = "puzzle";
  puzzleStage = stage;

  // Hide exploration objects
  player.visible = false;
  doorLock.visible = false;
  safe.visible = false;

  // Clear any previous puzzle options.
  clearOptions();

  if (stage === 1) {
    setupLock1(scene);
  } else if (stage === 2) {
    setupLock2(scene);
  }
}

