/**
 * Clears the puzzle option texts.
 */
function clearOptions() {
  optionTexts.forEach(opt => opt.destroy());
  optionTexts = [];
}

/**
 * Converts a float to its IEEE754 32-bit binary representation.
 */
function floatToIEEE754(num) {
  let f32 = new Float32Array(1);
  f32[0] = num;
  let u32 = new Uint32Array(f32.buffer);
  return u32[0].toString(2).padStart(32, '0');
}

/**
 * Flips one random bit in a binary string.
 */
function alterBit(binaryStr) {
  const index = Phaser.Math.Between(0, binaryStr.length - 1);
  const newBit = binaryStr[index] === '0' ? '1' : '0';
  return binaryStr.substring(0, index) + newBit + binaryStr.substring(index + 1);
}

/**
 * Computes the 8-bit two's complement representation of a signed number
 * using the "invert and add 1" method with bitwise operators.
 *
 * For n < 0:
 *   Uses ((~Math.abs(n) + 1) & 0xFF) to ensure the result fits in 8 bits.
 *
 * For n >= 0, simply returns the 8-bit binary string.
 */
function computeTwosComplement(n) {
  if (n >= 0) {
    return n.toString(2).padStart(8, '0');
  } else {
    // Use bitwise operations to compute the 8-bit two's complement.
    return ((~Math.abs(n) + 1) & 0xFF).toString(2).padStart(8, '0');
  }
}

