// Function to render a question
function renderQuestion() {
    // Clear any previous question (if it exists)
    if (questionText) {
        questionText.destroy();
    }
    answerTexts.forEach(function(textObj) {
        textObj.destroy();
    });
    answerTexts = [];

    // Get the current question object
    const currentQuestion = questions[currentQuestionIndex];

    // Add the question text at the top center of the screen
    questionText = this.add.text(400, 150, currentQuestion.questionText, { fontSize: '24px', fill: '#ffffff' });
    questionText.setOrigin(0.5);

    // Dynamically render answer options
    const startX = 200;
    const gapX = 200;
    const posY = 400;

    currentQuestion.answers.forEach((answer, index) => {
        let answerText = this.add.text(startX + index * gapX, posY, answer, { fontSize: '18px', fill: '#ffffff' });
        answerText.setOrigin(0.5);
        answerText.setInteractive(); // Enable input for this text
        // Add an event handler for when an answer is clicked
        answerText.on('pointerdown', () => {
            checkAnswer.call(this, index);
        });
        answerTexts.push(answerText);
    });
}

// Function to check the player's answer
function checkAnswer(selectedIndex) {
    const currentQuestion = questions[currentQuestionIndex];
    if (selectedIndex === currentQuestion.correct) {
        questionText.setText('Correct! Next question...');
    } else {
        questionText.setText('Incorrect. Next question...');
    }

    // Delay moving to the next question so the player can read the feedback
    this.time.delayedCall(2000, nextQuestion, [], this);
}

// Function to load the next question or restart the quiz
function nextQuestion() {
    currentQuestionIndex++;
    if (currentQuestionIndex >= questions.length) {
        // Optionally, you can restart the quiz or display a completion message
        questionText.setText('Quiz complete!');
        answerTexts.forEach(function(textObj) {
            textObj.destroy();
        });
    } else {
        renderQuestion.call(this);
    }
}

