    platforms.create(750, 220, null).setDisplaySize(200, 20).refreshBody();

    // Punkteanzeige
    pointsText = this.add.text(20, 20, 'Punkte: ' + points, { fontSize: '24px', fill: '#000' });

    // Spieler erstellen (als einfacher farbiger Kasten)
    player = this.physics.add.sprite(100, 450, null);
    player.displayWidth = 40;
    player.displayHeight = 60;
    player.setTint(0x0000ff); // Blauer Spieler
    player.setBounce(0.2);
    player.setCollideWorldBounds(true);
    this.physics.add.collider(player, platforms);

    // Steuerung per Cursor-Tasten
    cursors = this.input.keyboard.createCursorKeys();

    // Gegner erstellen
    enemyGroup = this.physics.add.group();
    // Beispiel: Erstelle zwei Gegner als rote Rechtecke
    var enemy1 = enemyGroup.create(400, 100, null);
    enemy1.displayWidth = 40;
    enemy1.displayHeight = 40;
    enemy1.setTint(0xff0000); // Roter Gegner
    enemy1.setCollideWorldBounds(true);
    enemy1.setBounce(1);
    enemy1.setVelocityX(100);

    var enemy2 = enemyGroup.create(700, 50, null);
    enemy2.displayWidth = 40;
    enemy2.displayHeight = 40;
    enemy2.setTint(0xff0000);
    enemy2.setCollideWorldBounds(true);
    enemy2.setBounce(1);
    enemy2.setVelocityX(-100);

    // Gegner sollen mit den Plattformen kollidieren
    this.physics.add.collider(enemyGroup, platforms);

    // berprfung, wenn der Spieler von einem Gegner berhrt wird
    this.physics.add.overlap(player, enemyGroup, onPlayerKilled, null, this);

    // Definiere eine Reihe von Matheaufgaben
    mathQuestions = [
        { question: 'Was ist 7 * 6?', correctAnswer: '42', options: ['36', '42', '48', '40'] },
        { question: 'Was ist 12 + 15?', correctAnswer: '27', options: ['25', '27', '30', '32'] },
        { question: 'Was ist 9 - 3?', correctAnswer: '6', options: ['3', '6', '9', '12'] },
        { question: 'Was ist 8 / 2?', correctAnswer: '4', options: ['2', '3', '4', '5'] }
    ];
}

function update() {
    // Spielersteuerung
    if (cursors.left.isDown) {
        player.setVelocityX(-160);
    } else if (cursors.right.isDown) {
        player.setVelocityX(160);
    } else {
        player.setVelocityX(0);
    }

    if (cursors.up.isDown && player.body.touching.down) {
        player.setVelocityY(-330);
    }

    // Gewinnbedingung: Wenn der Spieler ganz oben ist (z.B. y < 50) und noch nicht gewonnen hat
    if (player.y < 50 && !winAchieved) {
        winGame.call(this);
    }
}

function onPlayerKilled(player, enemy) {
    // Gegner "tten" den Spieler: Spiel pausieren und Matheaufgabe anzeigen
    this.physics.pause();
    player.setTint(0xff0000); // Spieler wird rot, um den "Tod" anzuzeigen

    // Zeige eine zufllige Matheaufgabe
    showMathQuestion.call(this);
}

function showMathQuestion() {
    // Whle zufllig eine Matheaufgabe aus
    currentMathQuestion = Phaser.Utils.Array.GetRandom(mathQuestions);

    // Erstelle einen halbtransparenten Hintergrund fr die Matheaufgabe
    var overlay = this.add.rectangle(400, 300, 800, 600, 0x000000, 0.5);

    // Zeige die Mathefrage
    var mathQuestionText = this.add.text(250, 200, currentMathQuestion.question, { fontSize: '32px', fill: '#fff' });

    // Erstelle Antwortoptionen als interaktive Texte
    var optionTexts = [];
    for (let i = 0; i < currentMathQuestion.options.length; i++) {
        let answer = currentMathQuestion.options[i];
        let optText = this.add.text(300, 260 + i * 40, (i + 1) + ') ' + answer, { fontSize: '28px', fill: '#fff' });
        optText.setInteractive({ useHandCursor: true });
        optText.on('pointerdown', () => {
            if (answer === currentMathQuestion.correctAnswer) {
                // Richtige Antwort: 10 Punkte hinzufgen
                points += 10;
                pointsText.setText('Punkte: ' + points);
                // Entferne die Matheaufgabe-Elemente und starte das Spiel neu
                overlay.destroy();
                mathQuestionText.destroy();
                optionTexts.forEach(text => text.destroy());
                restartGame.call(this);
            } else {
                // Falsche Antwort: Spieler behlt den "Tod"-Status
                mathQuestionText.setText('Falsch! Versuche es noch einmal: ' + currentMathQuestion.question);
            }
        });
        optionTexts.push(optText);
    }
}

function restartGame() {
    // Entferne alle Gegner (optional) und setze den Spieler zurck
    enemyGroup.clear(true, true);

    // Spieler zurcksetzen
    player.clearTint();
    player.x = 100;
    player.y = 450;

    // Physik fortsetzen
    this.physics.resume();

    // Gegner neu spawnen
    var enemy1 = enemyGroup.create(400, 100, null);
    enemy1.displayWidth = 40;
    enemy1.displayHeight = 40;
    enemy1.setTint(0xff0000);
    enemy1.setCollideWorldBounds(true);
    enemy1.setBounce(1);
    enemy1.setVelocityX(100);

    var enemy2 = enemyGroup.create(700, 50, null);
    enemy2.displayWidth = 40;
    enemy2.displayHeight = 40;
    enemy2.setTint(0xff0000);
    enemy2.setCollideWorldBounds(true);
    enemy2.setBounce(1);
    enemy2.setVelocityX(-100);

    this.physics.add.collider(enemyGroup, platforms);
}

function winGame() {
    winAchieved = true;
    // Punkte fr den Gewinn
    points += 50;
    pointsText.setText('Punkte: ' + points);

    // Erstelle Konfetti-Partikel
    var particles = this.add.particles('confetti');
    var emitter = particles.createEmitter({
        x: { min: 0, max: 800 },
        y: 0,
        angle: { min: 240, max: 300 },
        speed: { min: 100, max: 300 },
        gravityY: 300,
        lifespan: 4000,
        quantity: 10,
        scale: { start: 1, end: 0 },
        blendMode: 'ADD'
    });

    // Optionale Nachricht anzeigen
    this.add.text(200, 300, 'Gewonnen!', { fontSize: '48px', fill: '#000' });
}
