const config = {
  type: Phaser.AUTO,
  width: 800,
  height: 600,
  backgroundColor: '#f1f8e9',
  physics: {
    default: 'arcade',
    arcade: {
      gravity: { y: 300 },
      debug: false
    }
  },
  scene: { preload, create, update },
  parent: 'phaser-game'
};

const game = new Phaser.Game(config);

let player;
let platforms;
let cursors;
let stars;
let score = 0;
let scoreText;

function preload() {
  // Load the background image.
  this.load.image('sky', '/games/currency.png');
  // Load the platform image.
  this.load.image('ground', '/games/currency.png');
  // Load the collectible star image.
  this.load.image('star', '/games/currency.png');
  // Load the spritesheet for the player.
  // Ensure the 'dude.png' exists at this path and each frame is 32x48 pixels.
  this.load.spritesheet('dude', '/games/currency.png', { frameWidth: 32, frameHeight: 48 });
}

function create() {
  // Add the background image.
  this.add.image(400, 300, 'sky');

  // Create a static group for platforms.
  platforms = this.physics.add.staticGroup();
  platforms.create(400, 568, 'ground').setScale(2).refreshBody();
  platforms.create(600, 400, 'ground');
  platforms.create(50, 250, 'ground');
  platforms.create(750, 220, 'ground');

  // Create the player sprite and set its physics properties.
  player = this.physics.add.sprite(100, 450, 'dude');
  player.setBounce(0.2);
  player.setCollideWorldBounds(true);

  // Define animations for left and right movements.
  this.anims.create({
    key: 'left',
    frames: this.anims.generateFrameNumbers('dude', { start: 1, end: 1 }),
    frameRate: 10,
    repeat: -1
  });
  this.anims.create({
    key: 'right',
    frames: this.anims.generateFrameNumbers('dude', { start: 1, end: 1 }),
    frameRate: 10,
    repeat: -1
  });
  // Optionally define a "turn" animation if needed.
  this.anims.create({
    key: 'turn',
    frames: [{ key: 'dude', frame: 1 }],
    frameRate: 20
  });

  // Add collision between the player and the platforms.
  this.physics.add.collider(player, platforms);

  // Set up keyboard input.
  cursors = this.input.keyboard.createCursorKeys();

  // Create a group for collectible stars.
  stars = this.physics.add.group({
    key: 'star',
    repeat: 11,
    setXY: { x: 12, y: 0, stepX: 70 }
  });

  // Give each star a slight bounce.
  stars.children.iterate(function(child) {
    child.setBounceY(Phaser.Math.FloatBetween(0.4, 0.8));
  });

  // Add collision between stars and platforms.
  this.physics.add.collider(stars, platforms);
  // Check for overlap between the player and the stars.
  this.physics.add.overlap(player, stars, collectStar, null, this);

  // Display the score.
  scoreText = this.add.text(16, 16, 'Score: 0', { fontSize: '32px', fill: '#000' });
}

function update() {
  // Horizontal movement.
  if (cursors.left.isDown) {
    player.setVelocityX(-160);
    player.anims.play('left', true);
  } else if (cursors.right.isDown) {
    player.setVelocityX(160);
    player.anims.play('right', true);
  } else {
    player.setVelocityX(0);
    // Stop any current animation before setting the idle frame.
    player.anims.stop();
    player.setFrame(0);
  }

  // Allow the player to jump if they are touching the ground.
  if (cursors.up.isDown && player.body.touching.down) {
    player.setVelocityY(-330);
  }
}

// Callback when a star is collected.
function collectStar(player, star) {
  star.disableBody(true, true);
  score += 10;
  scoreText.setText('Score: ' + score);
}

const config = {
  type: Phaser.AUTO,
  width: 800,
  height: 600,
  backgroundColor: '#b3e5fc',
  physics: {
    default: 'arcade',
    arcade: {
      gravity: { y: 300 },
      debug: false
    }
  },
  scene: {
    preload,
    create,
    update
  }
};

const game = new Phaser.Game(config);

// Global variables for binary question UI, score, and level
let currentAnswers = [];
let correctAnswer = 0;
let questionActive = false;
let questionText;
let answerTexts = [];
let score = 0;
let scoreText;
let mistakes = [];
let level = 1;
let levelText;

function preload() {
  // Create a graphics object for drawing textures
  this.graphics = this.add.graphics();

  // Create a simple player texture (blue rectangle)
  this.graphics.fillStyle(0x0000ff, 1);
  this.graphics.fillRect(0, 0, 32, 48);
  this.graphics.generateTexture('player', 32, 48);
  this.graphics.clear();

  // Create a simple enemy texture (red rectangle)
  this.graphics.fillStyle(0xff0000, 1);
  this.graphics.fillRect(0, 0, 32, 32);
  this.graphics.generateTexture('enemy', 32, 32);
  this.graphics.clear();

  // Create a platform texture (green rectangle)
  this.graphics.fillStyle(0x00ff00, 1);
  this.graphics.fillRect(0, 0, 400, 32);
  this.graphics.generateTexture('platform', 400, 32);
  this.graphics.clear();
}

function create() {
  // Create platforms using a static physics group
  this.platforms = this.physics.add.staticGroup();
  // Ground platform (stretched to cover the bottom)
  this.platforms.create(400, 584, 'platform').setScale(2).refreshBody();
  // Additional platforms
  this.platforms.create(600, 400, 'platform');
  this.platforms.create(50, 250, 'platform');
  this.platforms.create(750, 220, 'platform');

  // Create the player sprite and enable physics
  this.player = this.physics.add.sprite(100, 450, 'player');
  this.player.setBounce(0.2);
  this.player.setCollideWorldBounds(true);

  // Create enemies group and add multiple enemies
  this.enemies = this.physics.add.group();
  // Enemy 1
  let enemy1 = this.enemies.create(700, 450, 'enemy');
  enemy1.setBounce(1);
  enemy1.setCollideWorldBounds(true);
  enemy1.setVelocityX(-100);
  // Enemy 2
  let enemy2 = this.enemies.create(400, 300, 'enemy');
  enemy2.setBounce(1);
  enemy2.setCollideWorldBounds(true);
  enemy2.setVelocityX(80);
  // Enemy 3
  let enemy3 = this.enemies.create(600, 150, 'enemy');
  enemy3.setBounce(1);
  enemy3.setCollideWorldBounds(true);
  enemy3.setVelocityX(-120);

  // Enable collisions between the player/enemies and the platforms
  this.physics.add.collider(this.player, this.platforms);
  this.physics.add.collider(this.enemies, this.platforms);

  // When an enemy overlaps the player, trigger the question routine
  this.physics.add.overlap(this.player, this.enemies, onEnemyHit, null, this);

  // Set up keyboard controls for player movement
  this.cursors = this.input.keyboard.createCursorKeys();

  // Create score text
  scoreText = this.add.text(16, 16, 'Score: 0', { fontSize: '20px', fill: '#000' });

  // Create level text
  levelText = this.add.text(16, 40, 'Level: 1', { fontSize: '20px', fill: '#000' });

  // Create a text object for showing the result (used for question feedback)
  this.resultText = this.add.text(400, 350, '', { font: '24px Arial', fill: '#000' }).setOrigin(0.5);

  // Listen for keyboard events (for answering binary questions)
  this.input.keyboard.on('keydown', handleKeyPress, this);
}

function update() {
  if (!questionActive) {
    // --- Player movement logic ---
    if (this.cursors.left.isDown) {
      this.player.setVelocityX(-160);
    } else if (this.cursors.right.isDown) {
      this.player.setVelocityX(160);
    } else {
      this.player.setVelocityX(0);
    }
    if (this.cursors.up.isDown && this.player.body.touching.down) {
      this.player.setVelocityY(-330);
    }

    // --- Enemy patrol logic ---
    this.enemies.getChildren().forEach(enemy => {
      if (enemy.body.blocked.left) {
        enemy.setVelocityX(Math.abs(enemy.body.velocity.x));
      } else if (enemy.body.blocked.right) {
        enemy.setVelocityX(-Math.abs(enemy.body.velocity.x));
      }
    });

    // --- Level progression logic ---
    // If the player reaches near the top of the screen, advance to the next level.
    if (this.player.y < 50) {
      nextLevel(this);
    }
  }
}

// This function is called when an enemy hits the player.
function onEnemyHit(player, enemy) {
  if (!questionActive) {
    questionActive = true;
    // Pause the physics to "freeze" the game during the question
    this.physics.pause();
    // Show the binary multiplication question UI
    generateBinaryQuestion(this);
  }
}

// This function generates and displays a binary multiplication question.
function generateBinaryQuestion(scene) {
  // Generate two random numbers (1 to 15) and convert them to binary strings.
  const a = Phaser.Math.Between(1, 15);
  const b = Phaser.Math.Between(1, 15);
  const aBinary = a.toString(2);
  const bBinary = b.toString(2);
  correctAnswer = a * b;

  // Display the question in the center of the screen.
  questionText = scene.add.text(
    400,
    200,
    `What is ${aBinary} * ${bBinary} in binary?`,
    { font: '24px Arial', fill: '#000' }
  ).setOrigin(0.5);

  // Create three answer options: the correct answer, one more, and one less.
  currentAnswers = [
    correctAnswer.toString(2),
    (correctAnswer + 1).toString(2),
    (correctAnswer - 1).toString(2)
  ];
  Phaser.Utils.Array.Shuffle(currentAnswers);

  // Display the answer options.
  answerTexts = [];
  for (let i = 0; i < currentAnswers.length; i++) {
    let optionText = scene.add.text(
      400,
      250 + i * 40,
      `${i + 1}: ${currentAnswers[i]}`,
      { font: '24px Arial', fill: '#000' }
    ).setOrigin(0.5);
    answerTexts.push(optionText);
  }
}

// This function handles key presses for answering the question.
function handleKeyPress(event) {
  // Only process input if a question is active.
  if (!questionActive) {
    return;
  }
  // Check if the pressed key is '1', '2', or '3'.
  const keyIndex = parseInt(event.key) - 1;
  if (keyIndex >= 0 && keyIndex < currentAnswers.length) {
    // Convert the chosen binary answer to an integer.
    const selectedAnswer = parseInt(currentAnswers[keyIndex], 2);
    if (selectedAnswer === correctAnswer) {
      // Correct answer
      this.resultText.setText('Correct!');
      score += 10;
      scoreText.setText('Score: ' + score);
      setTimeout(() => {
        resetQuestion(this);
      }, 500);
    } else {
      // Incorrect answer
      this.resultText.setText('Incorrect, try again!');
      mistakes.push(`Mistake: Selected ${currentAnswers[keyIndex]} instead of ${correctAnswer.toString(2)}`);
      console.log(mistakes);
      // Reset the question after a short delay to allow another try.
      setTimeout(() => {
        resetQuestion(this);
      }, 500);
    }
  }
}

// This function removes the question UI and resumes the game.
function resetQuestion(scene) {
  if (questionText) {
    questionText.destroy();
  }
  answerTexts.forEach(text => text.destroy());
  scene.resultText.setText('');
  questionActive = false;
  // Resume physics so the game continues.
  scene.physics.resume();
}

// This function advances the game to the next level.
function nextLevel(scene) {
  // Increase the level counter and update the display.
  level++;
  levelText.setText('Level: ' + level);

  // Optionally, add a new enemy to increase the challenge.
  let newEnemyX = Phaser.Math.Between(50, 750);
  let newEnemyY = Phaser.Math.Between(50, 300);
  let newEnemy = scene.enemies.create(newEnemyX, newEnemyY, 'enemy');
  newEnemy.setBounce(1);
  newEnemy.setCollideWorldBounds(true);
  // Set a random horizontal velocity between 100 and 150 (with a random direction).
  let velocityX = Phaser.Math.Between(100, 150);
  if (Phaser.Math.Between(0, 1)) {
    velocityX = -velocityX;
  }
  newEnemy.setVelocityX(velocityX);

  // Reset the player's position to the bottom of the screen.
  scene.player.setPosition(100, 450);
}

