    setupLock2(scene);
  }
}

/* ---------------------------
   LOCK 1: Door Lock Puzzle
   ---------------------------
   Convert a negative decimal (lock1Number) to its 8-bit 2's complement.
   This version uses the explicit invert-and-add-1 method implemented via bitwise operators.
*/
function setupLock1(scene) {
  // Generate a random negative integer between -128 and -1.
  lock1Number = Phaser.Math.Between(-128, -1);
  // Compute its 8-bit 2's complement using our new bitwise method.
  lock1Correct = computeTwosComplement(lock1Number);

  // Generate two distractors by flipping one random bit.
  let distractor1 = alterBit(lock1Correct);
  let distractor2 = alterBit(lock1Correct);
  while (distractor1 === lock1Correct) {
    distractor1 = alterBit(lock1Correct);
  }
  while (distractor2 === lock1Correct || distractor2 === distractor1) {
    distractor2 = alterBit(lock1Correct);
  }

  currentAnswers = [lock1Correct, distractor1, distractor2];
  Phaser.Utils.Array.Shuffle(currentAnswers);

  narrativeText.setText(
    `Door Lock Challenge:\n\n` +
    `A panel displays the decimal number:\n  ${lock1Number}\n\n` +
    `Convert it to its 8-bit 2's complement representation.`
  );

  displayOptions(scene);
}

/* ---------------------------
   LOCK 2: Safe Lock Puzzle
   ---------------------------
   Divide the door lock number (lock1Number) by a positive number decoded from an IEEE754 binary string.
*/
function setupLock2(scene) {
  // Generate a random positive float between 1 and 20.
  let randomFloat = Phaser.Math.FloatBetween(1, 20);
  randomFloat = parseFloat(randomFloat.toFixed(2));

  // Convert it to IEEE754 32-bit binary representation.
  ieee754Str = floatToIEEE754(randomFloat);
  ieee754Value = randomFloat;

  // Compute the division: (lock1Number) divided by (randomFloat).
  divisionResult = (lock1Number / randomFloat).toFixed(2);

  // Create two distractors by adding/subtracting a small random offset.
  let offset1 = parseFloat(Phaser.Math.FloatBetween(0.1, 1).toFixed(2));
  let offset2 = parseFloat(Phaser.Math.FloatBetween(0.1, 1).toFixed(2));
  let distractor1 = (parseFloat(divisionResult) + (Math.random() < 0.5 ? offset1 : -offset1)).toFixed(2);
  let distractor2 = (parseFloat(divisionResult) + (Math.random() < 0.5 ? offset2 : -offset2)).toFixed(2);

  if (distractor1 === divisionResult) distractor1 = (parseFloat(divisionResult) + 0.1).toFixed(2);
  if (distractor2 === divisionResult || distractor2 === distractor1) distractor2 = (parseFloat(divisionResult) - 0.1).toFixed(2);

  currentAnswers = [divisionResult, distractor1, distractor2];
  Phaser.Utils.Array.Shuffle(currentAnswers);

  narrativeText.setText(
    `Safe Challenge:\n\n` +
    `The safe displays an IEEE754 32-bit binary string:\n  ${ieee754Str}\n\n` +
    `This represents the number: ${ieee754Value}\n` +
    `Now, divide the door lock number (${lock1Number}) by this number.\n` +
    `Provide the result (rounded to two decimals) to open the safe.`
  );

  displayOptions(scene);
}

