  player.setCollideWorldBounds(true);
  // Remove the ceiling so the player can move off the top
  this.physics.world.checkCollision.up = false;

  // Set up collisions
  this.physics.add.collider(player, this.platforms, platformCollision, null, this);
  this.physics.add.collider(player, this.questionBlocks, hitQuestionBlock, null, this);

  // Set up input and camera follow
  cursors = this.input.keyboard.createCursorKeys();
  this.cameras.main.startFollow(player);

  // Generate the first platform and set up periodic platform generation
  generatePlatform.bind(this)();
  this.time.addEvent({ delay: 2000, callback: generatePlatform.bind(this), callbackScope: this, loop: true });
}

function update() {
  if (gameOver) return; // Stop update loop if the game is over

  // Handle player movement
  if (cursors.left.isDown) {
    player.setVelocityX(-160);
  } else if (cursors.right.isDown) {
    player.setVelocityX(160);
  } else {
    player.setVelocityX(0);
  }
  if (cursors.up.isDown && player.body.touching.down) {
    player.setVelocityY(-330);
  }

  // Check if the player has fallen below the visible screen
  if (player.y > this.cameras.main.scrollY + config.height) {
    gameOver = true;
    showDeathScreen(this);
  }
}

function generatePlatform() {
  const x = Phaser.Math.Between(50, 750);
  const y = highestPlatformY - Phaser.Math.Between(100, 200);
  const platform = this.platforms.create(x, y, 'platform').setScale(2, 1).refreshBody();
  highestPlatformY = y;

  // Randomly attach a question block above this platform with a 50% chance
  if (Phaser.Math.FloatBetween(0, 1) < 0.5) {
    createQuestionBlock(this, platform);
  }
}

function createQuestionBlock(scene, platform) {
  // Increase the vertical offset so the question block is higher above the platform.
  const verticalOffset = Phaser.Math.Between(50, 70); 
  // Add a slight horizontal variation (reduced compared to before).
  const horizontalOffset = Phaser.Math.Between(-10, 10); 
  const x = platform.x + horizontalOffset;
  const y = platform.y - verticalOffset;
  let qb = scene.questionBlocks.create(x, y, 'question_block').setScale(2, 1).refreshBody();
  qb.setData('questionIndex', Phaser.Math.Between(0, questions.length - 1));
}

function platformCollision(player, platform) {
  // Optional: Add custom logic for collisions with platforms if needed.
}

function hitQuestionBlock(player, block) {
  // Check for a collision from below and ensure no other question is active
  if (player.body.touching.up && block.body.touching.down && !currentQuestion) {
    const qIndex = block.getData('questionIndex');
    currentQuestion = questions[qIndex];
    block.disableBody(true, true);
    showQuestion(this, currentQuestion);
  }
}

function showQuestion(scene, question) {
  // Create an array to hold all UI elements related to the question
  scene.questionUI = [];

  // Display the question text and its background
  let qText = scene.add.text(100, 100, question.question, {
    fontSize: '20px',
    fill: '#333',
    wordWrap: { width: 600 }
  });
  scene.questionUI.push(qText);

  let qBg = scene.add.rectangle(
    qText.x - 10,
    qText.y - 10,
    qText.width + 20,
    qText.height + 20,
    0x000000,
    0.7
  ).setOrigin(0);
  scene.questionUI.push(qBg);

  // For each option, display the option text and its background
  question.options.forEach((option, index) => {
    let optText = scene.add.text(120, 160 + index * 40, option, {
      fontSize: '18px',
      fill: '#333'
    });
    scene.questionUI.push(optText);

    let optBg = scene.add.rectangle(
      optText.x - 10,
      optText.y - 10,
      optText.width + 20,
      optText.height + 20,
      0x000000,
      0.5
    ).setOrigin(0);
    scene.questionUI.push(optBg);

    // Make the option interactive; when clicked, check the answer
    optText.setInteractive();
    optText.on('pointerdown', () => checkAnswer(scene, index, question));
  });
}

function checkAnswer(scene, selectedIndex, question) {
  // Properly delete all UI elements associated with the question
  if (scene.questionUI) {
    scene.questionUI.forEach(element => element.destroy());
    scene.questionUI = null;
  }

  let feedbackImageKey = selectedIndex === question.answer ? 'option_correct' : 'option_wrong';
  let feedbackColor = selectedIndex === question.answer ? '#0f0' : '#f00';
  let feedbackTextString =
    selectedIndex === question.answer
      ? 'Richtig! ' + question.feedback
      : 'Falsch! ' + question.feedback;

  if (selectedIndex === question.answer) {
    // Note: Ensure that the function 'gameScore' is defined elsewhere.
    gameScore(gameName, 10);
    score += 10;
  }

  // Display feedback
  const feedbackBg = scene.add.rectangle(20, 400, 760, 60, 0x000000, 0.7).setOrigin(0);
  let feedbackText = scene.add.text(30, 410, feedbackTextString, {
    fontSize: '18px',
    fill: feedbackColor
  }).setDepth(100);
  feedbackBg.width = feedbackText.width + 20;
  feedbackBg.height = feedbackText.height + 20;
  scene.add.image(400, 300, feedbackImageKey).setScale(0.5).setDepth(90);

  currentQuestion = null;

  // Remove the feedback after 3 seconds
  scene.time.delayedCall(3000, () => {
    feedbackText.destroy();
    feedbackBg.destroy();
  });
}

function showDeathScreen(scene) {
  // Pause the physics and tint the player to indicate death.
  scene.physics.pause();
  player.setTint(0xff0000);

  // Display the "Game Over" screen
  const gameOverText = scene.add.text(
    scene.cameras.main.scrollX + config.width / 2,
    scene.cameras.main.scrollY + config.height / 2 - 50,
    'Game Over',
    { fontSize: '48px', fill: '#f00' }
  ).setOrigin(0.5);

  const restartText = scene.add.text(
    scene.cameras.main.scrollX + config.width / 2,
    scene.cameras.main.scrollY + config.height / 2 + 10,
    'Press SPACE to Restart',
    { fontSize: '24px', fill: '#fff' }
  ).setOrigin(0.5);

  // Wait for the player to press SPACE to restart the game
  scene.input.keyboard.once('keydown-SPACE', () => {
    scene.scene.restart();
    gameOver = false;
  });
}
